import { Component } from '@angular/core';
import {RoomsService} from "../../services/rooms.service";

@Component({
  selector: 'app-room-page',
  templateUrl: './room-page.component.html',
  styleUrls: ['./room-page.component.scss']
})
export class RoomPageComponent {
  title = 'Client'

  //rooms: IRoom[] = []


  loading = false

  constructor(public roomsService: RoomsService) {
  }

  ngOnInit(): void {
    this.loading = true
    // this.rooms$ = this.roomsService.getAll().pipe(
    //   tap(() => this.loading = false)
    // )
    //Or that way:
    this.roomsService.getAll().subscribe(() => {
      this.loading = false
    })
  }
}
<h1 class="font-bold mb-2 text-center">{{ title }}</h1>

<p *ngIf="loading" class="text-center text-lg">Loading...</p>

<app-room
  *ngFor="let room of roomsService.rooms"
  [room]="room"
></app-room>
