import {Component, Input} from "@angular/core";
import {IRoom} from "../../models/room";

@Component({
  selector: 'app-room',
  templateUrl: './room.component.html'
})
export class RoomComponent {
  @Input() room: IRoom
}
<div class="py-4 px-6 border rounded flex items-center flex-col mb-2">
  <img src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" alt = "Description" class="w-1/6 mb-2">
  <h2 class="mb-2 text-md text-center">{{ room.name }}</h2>
  <p>Capacity: {{ room.capacity }}</p>
  <p>Price: <span class="font-bold">{{ room.price | currency }}</span></p>
</div>
