import {Component, Input} from "@angular/core";
import {IRoom} from "../../models/room";

@Component({
  selector: 'app-room',
  templateUrl: './room.component.html'
})
export class RoomComponent {
  @Input() room: IRoom
}
<div class="py-4 px-6 border rounded flex items-center flex-row mb-2">
  <h2 class="mb-2 text-lg">{{ room.name }}</h2>
</div>
