import { Component } from '@angular/core';
import {IRoom} from "./models/room";
import {rooms as data} from "./data/rooms";
import {RoomsService} from "./services/rooms.service";
import {Observable, tap} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'Client'

  //rooms: IRoom[] = []
  rooms$: Observable<IRoom[]>

  loading = false

  constructor(private roomsService: RoomsService) {
  }

  ngOnInit(): void {
    this.loading = true
    this.rooms$ = this.roomsService.getAll().pipe(
      tap(() => this.loading = false)
    )
    // Or that way:
    // this.roomsService.getAll().subscribe(rooms => {
    //   this.rooms = rooms
    //   this.loading = false
    // })
  }
}


<div class="max-w-2xl container mx-auto pt-5">
  <h1 class="font-bold mb-2 text-center">{{ title }}</h1>

  <p *ngIf="loading" class="text-center text-lg">Loading...</p>

  <app-room
    *ngFor="let room of rooms$ | async"
    [room] = "room"
  ></app-room>
</div>
